    cc.sessionInformation.setSessionConfigs.asrTimeout ??= cc.sessionInformation.sessionConfigDefaults.asrTimeout

    //Build Voice Gateway advanced parameters object
    cc.sessionInformation.setSessionConfigs.advancedParameters = {
        "user": {
            "noInputMode": "event",
            "noInputTimeout": cc.sessionInformation.setSessionConfigs.userTimeout
        },
        "continuousAsr": {
            "asrEnabled": cc.sessionInformation.setSessionConfigs.asr,
