            "bargeInOnSpeech": cc.sessionInformation.setSessionConfigs.bargeInOnSpeech,
            "bargeInMinimunWords": 1
        }
    }
} catch(error) {
    api.log("error", `[BF_01_Set Session Configs; C: Extract session configs] Error Message: ${error.toString()}`)
}

